sys_ui_page.client_script:
var resourceId;
var xhttp = new XMLHttpRequest();

xhttp.open("GET", "/api/x_192597_tcc/get_resource_id/from_incident"+window.location.search, false, "admin", "1mNRKqgAnf9P");
xhttp.onreadystatechange = function() {
    if (this.readyState == 4 && this.status == 200) {
		resourceId = JSON.parse(xhttp.responseText).result.value;
    }
};
xhttp.send();

xhttp = new XMLHttpRequest();
xhttp.withCredentials = true;
xhttp.open("GET", "https://cmp-test.cireson.com/platform/api/Cireson_ConfigurationManager_Portal_Core_Models_ConfigurationManager_CmResource?$filter=ResourceId%20eq%20" + resourceId + "&$select=Id", true, 'Automation.Admin1', '00GAb00ga!1');
xhttp.onreadystatechange = function() {
	if (this.readyState == 4 && this.status == 200) {
		var id = JSON.parse(xhttp.responseText).value[0].Id;
		document.getElementById("tcc-frame").setAttribute('src', 'https://cmp-test.cireson.com/platform/app/iframe/CmDevice/' + id);
	}
};
xhttp.send();
